    static long maxWater(int []arr){
        int []lmax = new int[arr.length];
        int []rmax = new int[arr.length];
        lmax[0] = arr[0];
        long water=0;
        rmax[arr.length-1] = arr[arr.length-1];
        int rindex = arr.length-2;

        for(int i=1; i<arr.length; i++){
            lmax[i] = Math.max(lmax[i-1], arr[i]);
            rmax[rindex] = Math.max(rmax[rindex+1],arr[rindex]);
            rindex--;
        }

        for(int j=0; j<arr.length; j++){
            water = water + Math.min(lmax[j] , rmax[j]) - arr[j];
        }

        return water;

    }
